INSERT INTO `coffees` (`id`, `name`, `price`, `image`, `capacity`, `info`, `specific`, `ingredient`, `expired`, `id_brand`, `id_type`, `id_unit`, `created_at`, `updated_at`) VALUES
(4, 'Cà Phê Hạt Mộc Success 8 Trung Nguyên 250g', 250000, 'legendsuc8.jpg', 250, 'Thành phần chính của cà phê mộc success 8 được chọn lọc từ những hạt Arabica cao cấp, ngon nhất trong nước và trên thế giới.\r\nNguồn nguyên liệu tươi sạch, công nghệ sản xuất hiện đại bậc nhất cũng đóng vai trò quan trọng tạo nên cái đặc biệt trong hương vị của cà phê rang mộc, nguyên chất.\r\nMàu sắc: Màu nâu đặc trưng của sản phẩm, hạt rang chín đều, không cháy\r\nMùi vị: 100% hạt Arabica mang lại hương vị trái cây, tròn vị, thể chất mạnh nhưng cân bằng, hậu vị ngọt dịu. Được mệnh danh là “Vua cà phê”, cà phê Trung Nguyên là một trong những Thương hiệu làm nên cà phê Việt.\r\nKhi nhắc đến Cà phê, không ai không biết đến Trung nguyên, với đa dạng các sản phẩm và mẫu mã, phù hợp với các tiêu chí khác nhau, cà phê Trung Nguyên - điểm hẹn lý tưởng với những sản phẩm cao cấp và chất lượng nhất, luôn đáp ứng yêu cầu của người thưởng thức Cà phê.\r\nSẽ thật tuyệt vời khi có cảm giác tự xay, tự pha và chờ đợi từng giọt cà phê tí tách trong chiếc phin nhỏ, và rồi được tận hưởng những nét đặc trưng của dòng cà phê cao cấp, Cà Phê Hạt Mộc Success 8 Trung Nguyên.\r\n', 'Chưa rõ', '100% hạt arabica', 24, 1, 5, 1, '2019-10-24 20:00:00', NULL),
(5, 'Cà phê hạt Culi Robusta Trung Nguyên hạt số 1 bịch 1kg', 195000, 'tnculirobusta.jpg', 1000, 'Cà phê hạt culi Robusta Trung Nguyên, được chọn lọc từ những hạt cà phê tròn của giống Robusta, mang trong mình mùi thơm nhẹ nhàng, vị đắng hơi gắt\r\nThông thường, một quả cà phê sau khi thu hoạch trải qua sơ chế ta sẽ thu được 2 hạt cà phê dẹp thông thường. Cà phê hạt culi thì đặc biệt hơn khi chỉ cho ra 1 hạt cà phê duy nhất.\r\nTrên cùng một cây cà phê, các quả cà phê mang hạt culi sẽ chín sớm hơn so với những quả cà phê thông thường.\r\nQuả cà phê mang hạt culi có thời gian chín trên cành kéo dài hơn so với các quả cà phê thông thường. Tức là quả cà phê culi chín nẫu chậm và lâu hơn quả cà phê thông thường.\r\nHạt cà phê culi hầu như chỉ diễn ra trên các chủng loại, giống cà phê thông thường như Chery, Arabica và Robusta. Hạt cà phê culi trên giống cây Robusta được gọi là cà phê Culi Robusta, tương tự với giống cà phê Arabica, chúng được gọi là hạt Culi Arabica.\r\nCà phê hạt culi sau khi được tách riêng ra khỏi các hạt cà phê dẹp thông thường, cà phê hạt culi được bán trên thị trường như một dòng riêng biệt và được coi là một dòng hạt cà phê riêng. Trên thị trường cà phê hạt culi có giá thành cao hơn so với các hạt cà phê cùng chủng loại tương ứng.\r\n', 'Chưa rõ', 'Cà phê culi Robusta, dầu bơ, hương cà phê, hương chocolate', 24, 1, 5, 3, '2019-10-24 20:00:00', NULL),
(6, 'Cafe hạt Culi Arabica 1kg', 320000, 'culiara1kg.jpg', 1000, 'Từ những hạt Culi Arabica ngon nhất thế giới, bí quyết Phương Đông huyền bí, và sản xuất trên công nghệ hàng đầu, cà phê hạt xay Drip-Culi Arabica có mùi thơm rất độc đáo và vị êm nhẹ, ít đắng. Cu-li Arabica có tính a-xít cao hơn nên vị cũng chua mạnh hơn và ngon hơn so với Arabica thường. Tuy nhiên khi pha độ dày và sánh của lớp crema ít hơn. Đây là loại cà phê thượng hạng dành cho những người yêu cà phê.', 'Ít đắng và mùi thơm rất đặc trưng.', 'Cà phê hạt xay Drip - Culi Arabica ', 24, 1, 5, 3, '2019-10-25 03:00:00', NULL),
(7, 'Cafe Hạt Thượng hạng - Premium Culi', 265000, 'preculi1kg.jpg', 1000, 'Một loại cà phê tuyệt vời với dân sành café, dù bận rộn đến mấy, dù quán có xa đến đâu thì họ cũng sẵn sàng “cất công, mất sức” để đến thưởng thức cái hương vị café đặc trưng. Cà phê ở đây không chỉ là một loại thức uống mà là sự tận hưởng, trải nghiệm những hạt cà phê ngon nhất thế giới.\r\nCà phê hạt xay Drip - Premium Culi gồm những hạt Culi thượng hạng được của tất cả các loại cà phê Arabica, Robusta, Excelsa và Catimor, được Trung Nguyên sản xuất trên công nghệ hàng đầu và bí quyết không thể sao chép từ các chuyên gia trung nguyên nên có mùi vị rất thơm ngon, quyến rũ.\r\n', 'Cafe hạt thượng hạng - Premium Culi có nước pha màu cánh gián đậm,\r\nmùi thơm đậm và rất lâu.', 'Cafe hạt thượng hạng - Premium Culi ', 24, 1, 5, 3, '2019-10-25 03:00:00', NULL),
(8, 'Cà Phê Hạt Số 3 Trung Nguyên - Arabica Sẻ', 235000, 'so3tnse.jpg', 1000, 'Đối với những người sành cà phê, dù bận rộn đến mấy, dù quán có xa đến đâu thì họ cũng sẵn sàng “cất công, mất sức” để đến thưởng thức cái hương vị cà phê đặc trưng. Vì với họ, cà phê không chỉ là một loại thức uống mà là sự tận hưởng, trải nghiệm. Vì thế, bên cạnh những gói cà phê hòa tan chất lượng và tiện lợi thì người tiêu dùng vẫn có nhiều lựa chọn khác với cà phê hạt rang Trung Nguyên.\r\nArabica Sẻ chính là những hạt cà phê Arabica có kích thước theo một tiêu chuẩn nhất định và được thông qua các quá trình sàng lọc đặc biệt, trên công ngệ hàng đầu và bí quyết không thể sao chép từ các chuyên gia Trung Nguyên. Chính vì thế bạn có thể hoàn toàn an tâm về chất lượng. Với cà phê hạt xay Drip - Arabica Sẻ, bạn sẽ được thưởng thức những ly cà phê tuyệt hảo với mùi rất thơm, có vị chua nhẹ và màu nâu nhạt.\r\n', 'Chua ro', 'Chua ro', 24, 1, 5, 3, '2019-10-25 03:00:00', NULL),
(9, 'Cà Phê Drip - Chồn (Weasel)', 177000, 'dripchon.jpg', 1000, 'Là sự kết tinh tuyệt vời của nguồn nguyên liệu hạt ngon nhất, được chế biến bằng công nghệ ủ men sinh học hiện đại bậc nhất theo bí quyết phối trộn từ các chuyên gia hàng đầu. Tất cả tạo ra sự hòa quyện cân bằng, hoàn hảo của vị cà phê đặng mạnh mẽ và hương cà phê nồng nàn cho bạn trải nghiệm vị giác và xúc giác tuyệt vời, giúp cung cấp nguồn năng lượng sáng tạo.', 'Nước pha sánh đậm đà.Hương thơm đặc trưng của cà phê Chồn.\r\nLà một sự cân bằng hoàn hảo giữa hương và vị.\r\n', 'Chua ro', 24, 1, 5, 3, '2019-10-25 03:00:00', NULL),
(10, 'Cafe hạt Culi Robusta', 165000, 'culirobu.jpg', 1000, 'Culi Robusta có hương thơm tốt hơn, thiên về mùi hoa quả chín, mùi thảo dược, mùi mạch nha xen lẫn rất nhẹ và tinh tế nên mùi thơm khá hấp dẫn\r\nĐối với dân sành café, dù bận rộn đến mấy, dù quán có xa đến đâu thì họ cũng sẵn sàng “cất công, mất sức” để đến thưởng thức cái hương vị café đặc trưng. Café ở đây không chỉ là một loại thức uống mà là sự tận hưởng, trải nghiệm. Vì thế, bên cạnh những gói café hòa tan chất lượng và tiện lợi thì người tiêu dùng vẫn có nhiều lựa chọn khác với cà phê hạt rang Trung Nguyên.\r\n', 'Nước pha màu nâu đen.\r\nMùi thơm nhẹ, vị đắng hơi gắt.\r\n', 'Culi Robusta được chọn lọc từ những hạt cà phê tròn của giống Robusta.', 24, 1, 5, 3, '2019-10-25 03:00:00', NULL),
(11, 'Cà phê chồn Gia Lai', 17000, 'cafechongl.jpg', 500, 'Là 1 loại cà phê được rất nhiều người ưa chuộng trên nước.Trên thực tế chất lượng hạt cà phê sau khi qua dạ dày loài cầy vòi hương có thay đổi nhưng không nhiều. Nhìn chung vẫn giữ lại một phần hương vị cà phê nguyên chất, nếu chịu khó cảm nhận bạn có thể nếm được vị bùi bùi, dìu dịu vừa ngai ngái, phảng phất mùi của khói và hương vị sô cô la. Nhiều người uống loại cà phê này không chỉ vì hương vị mà còn vì đẳng cấp của nó.', 'Chua ro', 'Chua ro', 24, 1, 6, 3, '2019-10-25 03:00:00', NULL),
(12, 'Cà phê chồn túi lọc ', 200000, 'cafechontuiloc.jpg', 75, 'Cà phê Chồn túi lọc được pha trộn giữa cà phê Arabica( Mocha) với 10% cà phê Chồn tương ứng, tạo nên loại cà phê có nước đậm trung bình, hậu vị êm mượt, vị chua nhẹ nhàng, giàu mùi cacao đem lại cảm giác một ly café hoàn hảo chưa từng có.\r\nCà phê Con Chồn túi lọc – Sản phẩm mới, tiên phong cho dòng túi lọc. Tạo ưu điểm vượt trội và tiện lợi khi sử dụng.\r\nCà phê Chồn túi lọc cam kết dễ pha chế, không sử dụng chất bảo quản.', 'Chua ro', 'Chua ro', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(13, 'Cà phê chồn Weasel ', 17000000, 'cfchonweasel.jpg', 500, 'Là tặng phẩm đặc biệt chỉ dành cho những người thực sự Sành cà phê, những Nhà lãnh đạo, những Nhà kiến tạo tương lai của thế giới. Cà phê chồn nguyên liệu tham gia vào quá trình “bào chế” cà phê WEASEL được thu gom hoàn toàn từ tự nhiên, chọn lọc một cách tỉ mỷ và trải qua một quá trình siêu tiệt trùng đặc biệt trước khi tham gia vào công đoạn chế tác sản phẩm Cà phê WEASEL. Sản xuất giới hạn, mỗi năm chỉ 40 – 50kg.\r\nCà phê chồn Weasel Trung Nguyên là sản phẩm rất thích hợp để biếu, tặng trong các dịp Lễ Tết. Sản phẩm cafe chồn cao cấp nhằm mục đích, giới thiệu, quảng bá và làm quà tặng cho khách hàng hoặc đối tác ngoại giao trong dịp tổ chức sự kiện của các Doanh Nghiệp, Đoàn thể, Cá nhân hoặc Quà tết cổ truyền Việt Nam. Bởi sự Sang Trọng, Lịch Lãm và Đẳng Cấp.', 'Chua ro', 'Chua ro', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(14, 'Cà phê chồn Legend- Cà phê chồn nhân tạo', 800000, 'cfchonlegend.jpg', 500, 'Cà phê Legend Trung Nguyên là sản phâm cà phê chồn, nhưng không phải từ Cà phê phân chồn tự nhiên mà là nhờ vào những tiến bộ khoa học.\r\nCà phê chồn Legend là sản phẩm Cà phê cao cấp Trung Nguyên như một “bản giao hưởng của đất trời và con người” nơi đại ngàn Tây Nguyên hung vĩ, cà phê Trung Nguyên Legend là phiên bản đặc biệt của Cà phê Chồn Weasel “quý hiếm và đắt nhất thế giới”. Với hương thơm dịu như chiếc níu nhẹ nhàng lưu luyến, vị đắng thanh tao như khúc dư tình chợ phiên, cà phê Trung Nguyên Legend sở hữu hương và vị hết sức dị thường, khác biệt mà hiếm có loại cà phê nào trên thế giới sánh kịp.', 'Chua ro', 'Chua ro', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(15, 'Cà phê chồn Legend Revives Robusta', 2450000, 'caphechonlegebo.jpg', 1000, 'Cà phê Robusta được sử dụng để sản xuất ra cà phê chồn robusta được sản xuất từ những quả cà phê chin mọng ở vành đai cà phê Buôn Ma Thuột, thủ phủ cà phê Việt Nam. Cà phê Robusta tại Buôn Ma Thuột có hương vị đậm đà, lượng cafein cao, phù hợp với người có gu thưởng thúc cà phê mạnh.\r\nNhững hạt cà phê Robussta, sẽ được loài chồn lựa chọn tỉ mỉ, chặt chẽ nhờ khướu giác nhạy bén, loài chồn sẽ chỉ ăn những quả cà phê chin mọng nhất, trên nhưng cây cà phê khỏe nhất. Qua quá trình tiêu hóa của loài chồn, phần thịt cà phê sẽ được tiêu hóa chỉ còn lại hạt cà phê. Chúng sẽ ở lại trong dạ dày chồn và được ủ lên men tự nhiên nhờ enzyme đặc biệt có trong dạ dày chồn. Chính nhờ quá chình ủ lên men này, hương vị cà phê sẽ được nâng lên một đẳng cấp mới.\r\nCà phê chồn Robusta có hậu vị ngọt kéo dài trong nhiều giờ đồng hồ sau khi uống, mùi hương đậm đà của hạt robusta sẽ trở nên nồng nàn hơn và phảng phất trong đó là chút vị sô-cô-la ngọt ngào, khiến bao kẻ ghiền cà phê thích thú, say đắm. Để lại sự vấn vương, nhớ thương khôn nguôi mà dung ngôn từ thật khó để diễn tả hết.', 'Chua ro', 'Chua ro', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(16, 'Cà phê chồn Mocha Arabica ', 3250000, 'cfchonmochaara.jpg', 500, 'Cà phê chồn Mocha Arabica, là sản phẩm cà phê chồn nuôi, hộp quà cà phê cao cấp. Cà phê Mocha Arabica Cầu Đất là một trong ít nơi trên Việt Nam, trồng được giống cà phê Arabica, đây là vùng nguyên liệu truyền thống cao cấp được các nhà nhập khẩu cà phê trên thế giới săn tìm và ưu chuộng.\r\nCà phê Mocha Arabica, là loại cà phê cao cấp, giống cây cà phê chỉ trồng được ở độ cao 1600m so với mực nước biển. Vì thế ở Việt Nam, vùng trồng được rất hiếm. Chất lượng cà phê Mocha Arabica trồng tại Cầu Đất, Lâm Đồng được bạn bè thế giới đánh giá cao về chất lượng. Cà phê Mocha Arabica, có vị thanh nhẹ, chua, lượng cafein không quá cao, nhưng mùi hương rất đậm đà. Đây là loại hạt cà phê được các nước trên thế giới ưa chuộng và sử dụng nhiều.\r\nCà phê chồn Mocha Arabica, ngoài việc vẫn giữ được các đặc tính của cà phê Mocha Arabica nguyên thủy, còn được lên men trong da dày chồn, một loại enzyme đặc biệt trong dạ dày chồn hương tiết ra. Đưa hương vị cà phê chồn Mocha Arabica lên một đẳng cấp khác, một đẳng cấp của sự đặc ân của tự nhiên ban cho con người. Vương vấn chút nhẹ Sô-cô-la, không quá đậm để trở nên tinh tế. Vị chua được bổ sung thêm chút ngọt tạo nên một vị chua khác lạ mà bạn khó có thể tìm được ở sản phẩm cà phê truyền thống, một vị chua có chiều sâu đặc biệt.', 'Chua ro', 'Chua ro', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(17, 'Cà phê Chồn KING COFFEE WEASEL 225gam', 10000000, 'cfchonkingweasel.jpg', 500, 'Cà phê Chồn KING COFFEE WEASEL 225gam: Siêu Phẩm Cho Bậc Vĩ Nhân - Sản phẩm hội tụ đầy đủ những tinh hoa hợp nhất từ Âm và Dương, Đất và Trời, Con Người và Thiên Nhiên.\r\nĐược mệnh danh là loại cà phê quý hiếm và tinh túy bậc nhất thế giới, cà phê chồn hội tụ đầy đủ những tinh hoa hợp nhất từ Âm và Dương, Đất và Trời, Con Người và Thiên Nhiên.\r\nKING COFFEE WEASEL - Một siêu phẩm cà phê chồn được sáng tạo dựa trên tinh thần cống hiến, góp phần làm phong phú thêm đời sống trí tuệ thăng hoa của nhân loại. Hoàn toàn khác biệt so với những loại cà phê thông thường khác, KING COFFEE WEASEL là kết quả của quá trình tinh chế hoàn hảo với 9 giai đoạn kỳ công cùng sự tỉ mỉ đạt đến độ tinh xảo.\r\nĐể cảm nhận một cách đủ đầy nhất từng nốt hương vị khác biệt ẩn chứa sâu bên trong KING COFFEE WEASEL, hãy để cơ thể bạn hoàn toàn hòa quyện trong một không gian thật thanh tịnh. Âm hưởng của rừng già thoắt ẩn thoắt hiện bùng nổ mạnh mẽ trong tách cà phê đậm đà, nồng nàn hương thơm quyến rũ mà tao nhã, thức tỉnh tâm hồn, thăng hoa trí tuệ để kiến tạo nên những thành công vĩ đại cho nhân loại.', 'Chưa rõ', 'Cà phê Chồn 100% Arabica nguyên chất đã rang xay.', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(18, 'Cà phê Chồn Hạt Hoàng Gia 200G', 1900000, 'cfchonhoanggia.jpg', 200, 'Cà phê chồn hay cà phê phân chồn là tên một loại cà phê đặc biệt, một thứ đồ uống được xếp vào loại hiếm nhất trên thế giới. Loại cà phê này có nhiều ở Indonesia, trên các hòn đảo Sumatra, Java và Sulawesi. Từ Kopi Luwak được dùng để chỉ một loại hạt do loài cầy vòi đốm ăn quả cà phê rồi thải ra.\r\nKhi hạt cà phê do chồn hương ăn, thải ra được xử lý làm sạch mọi vết bẩn và yếu tố không an toàn thực phẩm, được rang theo một kỹ thuật đặc biệt( bí quyết riêng tạo ra hương vị café) thì sẽ có một loại cà phê chồn thành phẩm.\r\nHiện nay, Cà phê Chồn Hoàng Gia được làm từ 100% cà phê Robusta , khi qua tiêu hóa của Chồn cho ra loại cà phê Chồn Robusta. Khi thưởng thức cà phê Chồn Robusta Hoàng Gia, bạn cảm nhận đầy đủ hương và vị của cà phê.', 'Chưa rõ', 'Chưa rõ', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(19, 'Hộp cà phê cao cấp CHỒN TỰ NHIÊN 250gram', 9200000, 'cfcaocapchontn.jpg', 250, 'Vào khoảng gần 50 triệu đồng một kg Cà phê Chồn tự nhiên? Nghiêm túc đấy, Cà phê Chồn tự nhiên từ lâu được mệnh danh là cực phẩm từ thiên nhiên rất hiếm hoi trên thế giới. Hộp cafe cao cấp Chồn Tự Nhiên 250 gram chính là mối tâm giao tuyệt vời nhất mà bạn nên dành để tri ân cho khách quý của mình.\r\nĐiểm độc đáo và thượng lưu ở Hộp quà là cà phê chồn tự nhiên này chính là ở chỗ cà phê chồn tự nhiên có hương vị quá tuyệt vời và hoàn hảo. Hương và vị của cà phê chồn được đánh giá là đậm đà, lồng ghép vào đó là phảng phất mùi sô cô la. Bất kỳ ai khi thưởng thức cà phê chồn, đều sẽ cảm nhận được “vị mốc” đặc trưng, vị ngọt thơm như siro, và hương vị đậm đà kèm chút vị đắng vô cùng dễ chịu, và điều đó quả thật là quá tuyệt vời.\r\nXứng tầm là một loại cà phê thượng đẳng, cà phê chồn tự nhiên còn có sự hòa quyện của nhiều hương vị khác nhau. Loại cà phê chồn nguyên chất bên trong hộp quà cà phê cao cấp này chắc chắn sẽ mang đến những trải nghiệm thú vị cho người được tặng mỗi khi thưởng thức.', 'Chưa rõ', 'Chưa rõ', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(20, 'Cà phê chồn túi lọc Arabica ', 200000, 'cfchontuiloc.jpg', 75, 'Cũng giống như Cà phê Chồn Túi lọc Robusta thì Cà phê Chồn túi lọc Arabica cũng mang phong cách thưởng thức cà phê tinh tế thời hiện đại. Mang đến cho bạn một ly cafe hảo hạng và nguyên chất nhất ở mọi lúc mọi nơi, đủ đậm đà, đủ thơm ngon và quyến rũ, mê hồn không khác gì như ly cà phê phin truyền thống.\r\nNét độc đáo và đặc trưng nhất của Cà phê Chồn túi lọc Arabica - 75 gam đó là sự phối trộn giữa 10% cà phê Chồn Arabica và  90% Cà phê Robusta cao cấp tương ứng để tạo nên loại cà phê có nước đậm trung bình, vị chua nhẹ nhàng, giàu mùi cacao, cộng hưởng với cái vị hậu êm mượt đúng như gu thưởng thức cafe của người Việt chúng ta.\r\nCà phê Chồn Arabica túi lọc, nó cũng gần giống với túi lọc của trà vậy, và ở đây điểm khác biệt nhất chính là túi lọc cà phê lại không phải ngâm trong nước nóng trong khi pha chế như túi lọc của trà, mà nó còn có khi được gọi là phin giấy. ', 'Chưa rõ', 'Chưa rõ', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(21, 'Cà phê chồn túi lọc Robusta', 170000, 'caphechonrobu.jpg', 75, 'Cà phê Chồn túi lọc Robusta - phong cách thưởng thức cà phê tinh tế thời hiện đại. Mang đến cho bạn một ly cafe hảo hạng và nguyên chất nhất ở mọi lúc mọi nơi, đủ đậm đà, đủ thơm ngon và quyến rũ, mê hồn không khác gì như ly cà phê phin truyền thống.\r\nCà phê túi lọc có thể làm cho người thưởng thức cà phê theo lối pha truyền thống xưa nay còn hơi lạ lẫm. Thế nhưng, cuộc sống hiện đại, cà phê cao cấp được đánh giá là thứ cực phẩm được dành cho mọi thời đại, chính vì thế mà nó phải hợp thời, thời thượng và đẳng cấp hơn.\r\nCà phê Chồn túi lọc Robusta tại Huyền Thoại Việt là một trong những thương hiệu tiên phong tại Việt Nam, áp dụng công nghệ mới để tạo ra từng gói cà phê riêng lẻ, cho người ghiền cafe cơ hội thưởng thức ngay và luôn những ly cà phê nguyên chất sạch thơm ngon, đậm đà, và luôn tươi mới cũng thật dễ dàng và tiện dụng mọi lúc mọi nơi.\r\nTóm lại, Cà phê túi lọc Chồn Robusta là cà phê rang xay nguyên chất 90% và 10% Cà phê Chồn Robusta dạng bột được cho vào trong túi lọc sau đó được pha chế sử dụng. Đúng, đơn giản chỉ có vậy!', 'Chưa rõ', 'Chưa rõ', 24, 1, 6, 3, '2019-10-25 03:00:00', NULL),
(22, 'Cà phê chồn Combo 51 gram', 700000, 'cpchoncombo51.jpg', 50, 'Cà phê Combo chồn là sự kết hợp đồng điệu những nét đặc sắc nhất của hai loại cà phê tuyệt hảo Mocha và Robusta . Khi phối trộn giữa hương thơm đặc biệt của cà phê chồn Mocha Cầu Đất và vị đậm ngọt của cà phê chồn Robusta Buôn Ma Thuột, tạo nên sự khác biệt khi thưởng thức ly café hảo hạng.\r\nThành phần: 60% cà phê Chồn Mocha Cầu Đất - Đà Lạt kết hợp 40% cà phê Chồn Robusta Buôn Ma Thuột.\r\nĐặc trưng của cà phê Chồn Combo - 51 gam thứ thiệt là độ êm mượt, sánh mịn, mùi mốc tương thoang thoảng và hậu vị ngọt kéo dài, hoàn toàn khác biệt với bất cứ loại cà phê nào khác\r\nCà phê Chồn Combo 51 gam được bọc trong hộp quà vàng kim đính Logo đồng  sang trọng , vô cùng đặc biệt để trở thành món quà tặng độc đáo, ý nghĩa.\r\n', 'Chưa rõ', 'Chưa rõ', 24, 1, 6, 1, '2019-10-25 03:00:00', NULL),
(23, 'Cà phê hòa tan Wake up Hương Chồn 306g', 39000, 'wakeuphuongchon.jpg', 106, 'Wake up chắt lọc từ những hạt cà phê ngon nhất cùng bí quyết và công nghệ sản xuất hiện đại, Cà phê hương chồn Wake-up Vinacafé mang đến cho người dùng những trải nghiệm đầy tinh tế. Bên cạnh đó, với chiết xuất từ những hạt cà phê nguyên chất, sản phẩm đem đến những ly cà phê sánh quyện, đậm đặc cùng hương chồn thơm nồng nàn quyến rũ, giúp bạn luôn tỉnh táo để hoàn thành tốt mọi công việc.\r\nHãy khởi đầu ngày mới thật hăng hái cùng Wake - Up Cafe Sài Gòn với hương thơm cà phê ngất ngây, kết hợp vị hài hòa của cà phê và bột kem thơm mát, mang lại cho bạn sự tươi tỉnh, lạc quan để làm tốt những những công việc quan trọng và tận hưởng cuộc sống nhiều hơn.', 'Hương vị cà phê chồn thơm ngon, quyến rũ', 'Chưa rõ', 24, 3, 1, 1, '2019-10-25 03:00:00', NULL),
(24, 'Cà phê hòa tan G7 - Cappuccino hương Chocolate', 72000, 'g7hoatandentn.jpg', 100, 'Mô tả: Bạn đã từng nghe: \" Coffee should be black as Hell, Strong as Death and Swess as Love\" - Cà phê phải đen như địa ngục, đắng như tử thần, ngọt ngào như tình ái\r\nNhưng cappuccino thì không như vậy, một chút sắc đen thẫm và vị đắng của cafe, một chút sữa nóng làm mềm cái đắng ấy, một chút trắng tinh khôi và ngọt ngào của bột sữa đánh bông, một chút hương chocolate thoang thoảng, hay mùi hạt dẻ thơm nồng. Tất cả tạo nên cốc cafe Ý ưa thích ngọt thơm, nhẹ nhàng và mềm mại. Cà phê hoà tan G7 CAPPUCCINO hội tụ những yếu tố đặc biệt ấy bởi được lưa chọn từ những nguyên liệu tốt, công nghệ sản xuất hiện đại, bí quyết Phương Đông độc đáo. Cafe cappuccino được chắt lọc tinh túy từ những hạt cà phê ngon nhất Buôn Ma Thuột kết hợp bột kem và các nguyên liệu cao cấp khác, cộng với bí quyết độc đáo của Trung Nguyên, mang đến những người đam mê cà phê một loại cà phê hòa tan Cappuccino được pha chế theo phong cách Ý', 'Mùi thơm đan xen với socola.', 'Chưa rõ', 24, 1, 1, 1, '2019-10-25 03:00:00', NULL),
(25, 'Cà Phê G7 Hòa Tan Đen Trung Nguyên Hộp 50 Gói', 65000, 'g7htdtn50g.jpg', 30, 'Cà phê G7 hòa tan đen là sản phẩm duy nhất được lựa chọn phục vụ các nguyên thủ quốc gia tại ASEM 5. Vì vậy không phải cần là nguyên thủ, bạn có thể uống loại cà phê này bất cứ lúc nào vì sự tiện lợi và hương vị khó quên của nó. Sản phẩm sẽ ngon hơn khi uống cùng đá lạnh hoặc cho thêm sữa tùy khẩu vị.\r\nCà phê hòa tan G7 Hòa tan đen của Trung Nguyên được chọn lựa từ những hạt cà phê tươi, xanh, sạch và thuần khiết nhất. Cây cà phê được nuôi dưỡng trên mảnh đất bazan Buôn Mê Thuột màu mỡ, được chăm sóc kỹ càng và sạch hoàn toàn để các hạt cà phê khi được lựa chọn phải mang tròn hương vị vốn có mà không pha tạp thêm bất kỳ thứ gì khác.', 'Chua nhẹ, vị đậm đắng, thơm, ngọt.', 'Abrabica, Robusta, Excelsa, Catimor.', 24, 1, 1, 1, '2019-10-25 03:00:00', NULL),
(26, 'Cà phê Sáng tạo 1 Trung Nguyên - 340 gram', 45000, 'cpst1tn.jpg', 340, 'Cà phê sáng tạo 1 của Trung Nguyên sau khi pha chế, cà phê có nước pha màu nâu cánh gián đậm, mùi thơm nhẹ, vị đắng hơi gắt đặc trưng của giống cà phê Robusta.\r\nĐược chế biến từ những hạt cà phê Culi Robusta ngon nhất của vùng đất Thánh địa cà phê Buôn Ma Thuột cùng những công nghệ hiện đại nhất thế giới và bí quyết riêng không thể sao chép, Sáng tạo số 1 mang hương thơm dịu lưu luyến, vị đậm đà mân mê vị giác chuyên dành cho những người sành cà phê và các quán cà phê muốn sở hữu gu cà phê riêng biệt.\r\nĐặc biệt, phối trộn các loại theo công thức và hướng dẫn đặc biệt từ các chuyên gia cà phê Trung Nguyên còn có thể giúp tạo ra gu uống cà phê riêng, mang hương vị đặc trưng chỉ có ở quán bạn.', 'Vị đắng hơi gắt, mùi thơm dịu nhẹ với nước pha màu nâu cánh gián đậm', 'Culi Robusta', 24, 1, 2, 3, '2019-10-25 03:00:00', NULL),
(27, 'Cà Phê Sức Sống (Nâu) - 500gr', 60000, 'cpssnau.jpg', 500, 'Ví như sức sống mãnh liệt của đại ngàn, trong bạn luôn tiềm ẩn nguồn năng lượng vô biên. Nhấp một cụm cà phê Trung Nguyên – Nâu, bạn sẽ thấy tràn đầy sinh lực, sẵn sàng để hành động hơn bao giờ hết.\r\nĐược sáng tạo bởi sự kết hợp 4 loại hạt Arabica, Robusta, Excelsa, Catimor tốt nhất, cũng như công nghệ chế biến cà phê hàng đầu thế giới và bí quyết không thể sao chép, cà phê Trung Nguyên – Nâu mang mùi hương thơm nồng quyến rũ, vị đậm đà đặc trưng sẽ đánh thức những ý tưởng sáng tạo tiềm ẩn và SỨC SỐNG ĐẠI NGÀN còn đang ngủ quên trong bạn.\r\nPhù hợp với mọi cách uống, thích hợp hơn cho những người có \"gu\" uống cà phê đậm.\r\n', 'Mùi hương thơm nồng quyến rũ.Vị đậm đà đặc trưng.', 'Arabica, Robusta, Excelsa và Catimor.', 24, 1, 2, 3, '2019-10-25 03:00:00', NULL),
(28, 'Cà Phê S (Chinh Phục) 5 Gói 100gr', 40000, 'caphescp.jpg', 100, 'Con đường CHINH PHỤC THÀNH CÔNG vốn không dễ dàng nhưng với tinh thần chiến binh quả cảm của chính bạn và sự hậu thuẫn vững chắc bằng chất lượng tuyệt hảo của cà phê Trung Nguyên S sẽ tiếp sức và đánh thức những ý tưởng sáng tạo đột phá để thực hiện những giấc mơ.\r\nVới công thức phối trộn đặc biệt dựa trên sự kết hợp 4 loại hạt Arabica, Robusta, Excelsa, Catimor tốt nhất cũng những công nghệ ché biến cà phê hàng đầuThế giới và bí quyết không thể sao chép, cà phê Trung Nguyên S có màu nước nâu sánh, hương thơm đầy, vị đậm chình là một người bạn đồng hành đích thực trên con đường CHINH PHỤC THÀNH CÔNG.\r\nThích hợp cho những người có \"gu\" uống cà phê đậm. Phù hợp với mọi cách uống.\r\n', 'Cà phê Trung Nguyên S có màu nước nâu sánh, hương thơm đầy, vị đậm đà.', 'Chưa rõ', 24, 1, 2, 3, '2019-10-25 03:00:00', NULL),
(29, 'Cà phê hòa tan NesCafé 3 trong 1 đậm đà hài hòa 340g (17g x 20 gói)', 50000, 'nes3in1340g.jpg', 340, 'Cà phê hòa tan Nescafé 3 trong 1 đậm đà hài hòa được pha trộn theo tỷ lệ thích hợp cùng sữa và bột kem, đáp ứng sở thích thưởng thức của đại đa số người tiêu dùng\r\nSản phẩm mang tới một trải nghiệm đặc biệt cho những tín đồ cà phê tại Việt Nam, bên cạnh vị thơm ngon khó cưỡng, điều khác biệt so với cà phê hòa tan thông thường đó là bạn sẽ thấy được lớp cà phê rang xay nhuyễn mỏng mịn còn đọng lại dưới đáy ly cà phê, là minh chứng cho cà phê rang xay nghiền nhuyễn.\r\nSản phẩm được làm từ 100% hạt cà phê Việt Nam chất lượng cao, gieo trồng và canh tác theo kỹ thuật của Nescafé Plan và sản xuất theo công nghệ nghiền nhuyễn hạt cà phê (MRC - Micronized Roasted Coffee) độc quyền của Nestle.', 'Thơm, dễ chịu ,đậm đà hài hòa. Gu cà phê hài hòa giữa vị cà phê và vị sữa', 'Đường, bột kem pha cà phê, muối, hương cà phê tổng hợp, chất điều chỉnh độ chua', 24, 2, 1, 1, '2019-10-25 03:00:00', NULL),
(30, 'Cà phê hòa tan NesCafé 3 trong 1 đậm vị cà phê 340g (17g x 20 gói)', 50000, 'nes3in1dv340g.jpg', 340, 'Cà phê hòa tan Nescafe 3 trong 1 đậm vị cà phê hộp 20 gói được nghiên cứu riêng dành cho những người thích uống cà phê vị đậm, đắng và hương thơm mạnh mẽ đặc trưng của cà phê nguyên chất\r\nSản phẩm mang tới một trải nghiệm đặc biệt cho những tín đồ cà phê tại Việt Nam, bên cạnh vị thơm ngon khó cưỡng, điều khác biệt so với cà phê hòa tan thông thường đó là bạn sẽ thấy được lớp cà phê rang xay nhuyễn mỏng mịn còn đọng lại dưới đáy ly cà phê, là minh chứng cho cà phê rang xay nghiền nhuyễn.\r\nSản phẩm được làm từ 100% hạt cà phê Việt Nam chất lượng cao, gieo trồng và canh tác theo kỹ thuật của Nescafe Plan và sản xuất theo công nghệ nghiền nhuyễn hạt cà phê (MRC - Micronized Roasted Coffee) độc quyền của Nestle. Bên cạnh các thành phần như cà phê hòa tan, đường, kem sữa truyền thống, sản phẩm mới được bổ xung thêm hạt cà phê rang xay nhuyễn với lớp bọc coffee creamer. Khi cà phê rang xay nhuyễn hòa tan với nước sôi bung nở, sẽ làm bật dậy hương thơm đặc trưng của cà phê rang xay. Bên cạnh đó cà phê rang xay nhuyễn hòa quyện cùng vị sữa sẽ cho ly cà phê đậm đà hơn với vị ngon khó cưỡng.\r\n', 'Đậm đà vị cà phê, Gu cà phê mạnh đặc trưng', 'Đường, bột kem pha cà phê, chất làm dày 1440, muối i-ốt, hương tổng hợp dùng trong thực phẩm, chất điều chỉnh độ chua', 24, 2, 1, 1, '2019-10-25 03:00:00', NULL),
(31, 'Cà phê sữa hòa tan NesCafé Cà phê sữa đá 3 in 1 200g (20g x 10 gói)', 38000, 'nes3in1sd340g.jpg', 200, 'Cà phê sữa hòa tan NesCafe cà phê sữa đá 3 in 1 hộp 10 gói là sự kết hợp hoàn hảo giữa vị đắng đặc trưng của cà phê và vị béo ngọt thơm ngon của sữa, mang đến cho bạn những tách cà phê đậm đà khó cưỡng. Sản phẩm giúp bạn khởi đầu một ngày mới tươi vui và năng động hơn, tăng cường khả năng tập trung khi làm việc hiệu quả.\r\nSản phẩm được tuyển chọn và sản xuất từ những hạt cà phê Việt Nam chất lượng cao và rang ở nhiệt độ lý tưởng nhằm đảm bảo dậy mùi và có hương vị thơm ngon hảo hạng nguyên chất.', ': Cà phê sữa đá đậm đà chất cà phê, hương vị cà phê mạnh mẽ, đậm đà sánh quyện với vị béo ngọt dịu nhẹ của sữa thật.', '100% cà phê Việt Nam nguyên chất', 24, 2, 1, 1, '2019-10-25 03:00:00', NULL),
(32, 'Nescafé Café Việt Cà Phê Rang Xay Vị Nguyên Chất 1 (250g)', 52000, 'nesrxvnc250g.jpg', 250, 'Nescafé Café Việt Cà Phê Rang Xay Vị Nguyên Chất 1 tự hào mang đến cà phê rang xay được sản xuất từ 100% cà phê sạch Robusta Việt Nam tại những vùng cà phê nổi tiếng Đắc Lắc, Lâm Đồng, Đắc Nông, Gia Lai và chọn lọc kỹ từ những trái cà phê sạch chín đỏ không nấm mốc và hóa chất độc hại.\r\nKết hợp cùng công nghệ rang đặc biệt, tỉ mỉ trong thời gian rang và nhiệt độ rang, không tẩm ướt trong suốt quá trình rang để mang đến hạt cà phê rang nguyên chất, sạch sẽ trong suốt quá trình chế biến.\r\nMang lại cho những người sành cà phê Việt Nam một tách cà phê pha phin ngon và sạch với hương thơm quyến rũ, vị cà phê nguyên chất.\r\n', 'Vị cân bằng, hậu vị ngọt thanh, thơm quyến rũ, thoảng mùi caramel', 'Cà phê rang xay nguyên chất (95%),bột cacao, hương thực phẩm tổng hợp', 24, 2, 2, 3, '2019-10-25 03:00:00', NULL),
(33, 'Nescafé Café Việt Cà Phê Rang Xay Vị Nguyên Chất 2 (250g)', 52000, 'nesrxvnc250g2.jpg', 250, 'Nescafé Café Việt Cà Phê Rang Xay Vị Nguyên Chất 2 tự hào mang đến cà phê rang xay được sản xuất từ 100% cà phê sạch Robusta Việt Nam tại những vùng cà phê nổi tiếng Đắc Lắc, Lâm Đồng, Đắc Nông, Gia Lai và chọn lọc kỹ từ những trái cà phê sạch chín đỏ không nấm mốc và hóa chất độc hại.\r\nKết hợp cùng công nghệ rang đặc biệt, tỉ mỉ trong thời gian rang và nhiệt độ rang, không tẩm ướt trong suốt quá trình rang để mang đến hạt cà phê rang nguyên chất, sạch sẽ trong suốt quá trình chế biến.\r\nMang lại cho những người sành cà phê Việt Nam một tách cà phê pha phin ngon và sạch với hương thơm quyến rũ, vị cà phê nguyên chất.', 'Đậm vị cà phê, hậu vị đắng êm, hương thơm cà phê rang xay nồng.', 'Cà phê rang xay nguyên chất (99%), hương thực phẩm tổng hợp', 24, 2, 2, 3, '2019-10-25 03:00:00', NULL),
(34, 'Cà phê sữa NesCafé Espresso Roast 180ml', 12500, 'nesesro180.jpg', 180, 'Cà phê sữa Nescafe Espresso Roast lon 180ml được sản xuất từ hạt cà phê thượng hạng, mang đến cho bạn một hương vị cà phê mới, đậm đà hơn, sảng khoái hơn. Một lon cà phê mát lạnh tiện lợi sẽ là lựa chọn mới cho mọi người nơi công sở, giúp bạn tập trung và cảm thấy hưng phấn khi bắt đầu một ngày làm việc hay học tập.\r\nSản phẩm đã được đóng lon rất tiện dụng vì có thể sử dụng mọi lúc mọi nơi mà không mất thời gian pha chế. Đây còn là sản phẩm chất lượng cao và hợp vệ sinh, được sản xuất theo công nghệ tiên tiến, không sử dụng các chất hóa học, chất gây hại cho sức khỏe nên bạn hoàn toàn yên tâm sử dụng để bổ sung năng lượng mỗi ngày.', 'Hương vị cà phê Espresso đậm đà hòa quyện cùng vị sữa nhẹ nhàng.', 'Nước, đường, chiết xuất cà phê (4,4%), sữa bột tách kem, chất béo sữa, chất điều chỉnh độ acid,...', 24, 2, 3, 2, '2019-10-25 03:00:00', NULL),
(35, 'Cà phê đen NesCafé Latte 180ml', 12500, 'neslat180.png', 180, 'Cà phê uống liền Nescafe Latte là sản phẩm cà phê uống liền mang phong cách hiện đại, được sản xuất từ hạt cà phê thượng hạng, mang đến cho bạn một hương vị cà phê mới, đậm đà hơn, sảng khoái hơn. Một lon cà phê Nescafe thơm ngon có thể giúp bạn tập trung và cảm thấy hưng phấn khi bắt đầu một ngày làm việc hay học tập.\r\nCà phê uống liền Nescafé Latte được đóng lon 180ml tiện dụng, có thể sử dụng mọi lúc mọi nơi mà không mất thời gian pha chế.', 'Dành cho những người thích gu cà phê Latte thơm béo nhiều sữa, hài hòa vị cà phê.', ': Đường, nước, chiết xuất cà phê, chất béo sữa, hương cà phê giống tự nhiên, sữa bột tách kem, chất điều chỉnh độ axit,...', 24, 2, 3, 2, '2019-10-25 03:00:00', NULL),
(36, 'Cà phê đen NesCafé Café Việt 170ml', 12500, 'nescaviet170.jpg', 170, 'Cà phê hòa tan NesCafé Café Việt là sản phẩm cà phê uống liền mang phong cách hiện đại, được sản xuất từ 100% từ những hạt cà phệ Việt Nam chất lượng, được tuyển chọn và sàng lọc, rang xay theo công nghệ độc quyền của NesCafé, mang đến cho bạn một hương vị cà phê mới, đậm đà hơn, sảng khoái hơn.\r\nMột lon cà phê NesCafé thơm ngon có thể giúp bạn tập trung và cảm thấy hưng phấn khi bắt đầu một ngày làm việc hay học tập.\r\nCà phê uống liền NesCafé Việt được đóng lon tiện dụng, có thể sử dụng mọi lúc mọi nơi mà không mất thời gian pha chế.\r\n', 'Làm từ nước cốt cà phê tươi mang đến hương vị cà phê đen rang xay thuần tuý kiểu Việt với hương cà phê thơm nồng, ngọt hậu và đắng nhẹ.', 'Nước, nước cốt hỗn hợp cà phê rang xay, đường, chất điều chỉnh độ chua, hương tổng hợp, muối i-ốt, chất ổn định 415, chất tạo ngọt tổng hợp 950', 24, 2, 3, 2, '2019-10-25 03:00:00', NULL),
(37, 'Cà phê sữa NesCafé 170ml', 12000, 'cafenessuada170.jpg', 170, 'Cà phê uống liền Nescafe Latte lon 170 ml là sản phẩm cà phê uống liền mang phong cách hiện đại, được sản xuất từ hạt cà phê thượng hạng, mang đến cho bạn một hương vị cà phê mới, đậm đà hơn. Chỉ với một lon Nescafe Latte sẽ giúp bạn tỉnh táo và tập trung vào công việc ngay tức thì.\r\nCà phê pha sẵn từ Nescafe có nhiều hương vị đa dạng, cho bạn nhiều sự lựa chọn: Nescafe\' Espresso Roast (Vị Cà phê đậm đà), Nescafe\' Cafe Việt (Cà phê đen kiểu Việt), Nescafe\' Extra Rich (gu cà phê đậm). Cà phê uống liền Nescafe Latte lon 180 ml có hương vị Cà phê Sữa béo ngọt, thích hợp cho những bạn thích sự hòa quyện của sữa và cà phê.', 'Kết hợp hoàn hảo giữa sữa thật và nước cốt cà phê tươi từ 100% hạt cà phê Việt Nam chất lượng, mang đến hương vị cà phê sữa đá Việt Nam truyền thống.', 'Nước, nước cốt hỗn hợp cà phê rang xay, đường, bột kem pha cà phê,...', 24, 2, 3, 2, '2019-10-25 03:00:00', NULL),
(38, 'Bột Kem Nestle Coffee Mate (450g)', 62000, 'botkemnescofmate450.jpg', 450, 'Bột Kem Nestle Coffee Mate (450g) là loại bột kem không làm từ sữa đầu tiên, được ưa chuộng trên thế giới nhờ khả năng tạo béo nhưng không ngọt, thêm phần đậm đà cho tách cà phê của bạn. Bột kem này có thể dùng được cho người ăn kiêng vì không chứa Cholesterol.\r\n\r\nBột Kem Nestle Coffee Mate được điều chế hương vị đặc biệt, có khả năng tạo béo nhưng không gây ngọt. Nhờ đó, bột kem có thể dễ dàng phù hợp với khẩu vị của hầu hết người dùng.\r\nBột Kem Nestle Coffee Mate là thành phần đặc biệt có thể thay đổi tách cà phê bình thường thành tách cà phê đậm đà theo đúng nghĩa. Hương vị kem mịn sẽ kết hợp cùng cà phê đắng đậm và thơm lừng đánh thức mọi khứu giác của người thưởng thức. Sản phẩm ngon hơn khi uống nóng.\r\n', 'Thơm, béo, thích hợp dùng chung với cà phê', 'Bột kem, sữa, muối, chất điều chỉnh…', 24, 2, 4, 1, '2019-10-25 03:00:00', NULL),
(39, 'Bột kem pha cafe Mỹ Nestlé Coffee Mate 1.41kg', 380000, 'botkemmynetcoma141.jpg', 1410, 'Hàng quán cafe luôn kích thích bạn ghé thăm không chỉ thu hút bạn bởi không gian thơ mộng, độc lạ hay để bạn thoả sức “sống ảo”. Mỗi ly cà phe còn mang đến cho bạn sự hấp dẫn riêng của nó bởi hương vị đặc trưng của từng quán.\r\nNhăm nhi 1 tách cafe cùng người thân, bạn bè, cảm giác tự tay pha thật tuyệt vời và ý nghĩa biết bao. Bí quyết để có ly cafe vừa chất lượng, vừa thơm béo là bột kem pha cafe Mỹ Nestle Coffee Mate.\r\nBột kem Nestlé Coffee-mate tạo béo nhưng không gây ngọt, dễ dàng phù hợp với mọi khẩu vị của người tiêu dùng. Nếu bạn là người thích ngọt thì vẫn có thể thêm một ít đường hoặc sữa cho vừa miệng.\r\n', 'Thơm, béo, thích hợp dùng chung với cà phê', 'Bột kem, sữa, muối, chất điều chỉnh…', 24, 2, 4, 1, '2019-10-25 03:00:00', NULL),
(40, 'Cà Phê Sữa Hoà Tan Café deVIN 3in1 (Hộp 10 gói x 17g)', 26000, 'deVIN3in1.jpg', 170, 'Cà phê hòa tan deVIN 3 trong 1 đậm đà hài hòa được pha trộn theo tỷ lệ thích hợp cùng sữa và bột kem, đáp ứng sở thích thưởng thức của đại đa số người tiêu dùng\r\nSản phẩm mang tới một trải nghiệm đặc biệt cho những tín đồ cà phê tại Việt Nam, bên cạnh vị thơm ngon khó cưỡng, điều khác biệt so với cà phê hòa tan thông thường đó là bạn sẽ thấy được lớp cà phê rang xay nhuyễn mỏng mịn còn đọng lại dưới đáy ly cà phê, là minh chứng cho cà phê rang xay nghiền nhuyễn.\r\nSản phẩm được làm từ 100% hạt cà phê Việt Nam chất lượng cao\r\n', 'Thơm, ngon, mùi vị dễ chịu', 'Đường, caramel, bột kem, cà phê hoà tan (12%), sữa bột, muối, hương cà phê', 12, 4, 1, 1, '2019-10-26 03:00:00', NULL),
(41, 'Lon Vinacafé Cà Phê Rang Xay (500g)', 120000, 'vinacoffee-rangxay500g.jpg', 500, 'Lon Vinacafé Cà Phê Rang Xay (500g) - Bí quyết để có ly cà phê rang xay ngon theo kiểu Việt Nam là ở khâu lựa chọn nguyên liệu hạt, cách thức phối trộn và điều chỉnh chế độ rang xay phù hợp.\r\nại Vinacafé, với hơn 50 năm kinh nghiệm hiểu biết sâu sắc về hạt cà phê Việt Nam (từ năm 1968), các chuyên gia đã trình diễn các bí quyết trên trong dòng sản phẩm mới Vinacafé Cà Phê Rang Xay để làm nên hương vị đâm đà của cà phê rang xay 100% nguyên chất đúng kiểu Việt Nam.\r\n', 'Đậm đà kiểu cà phê rang, ngon, ngọt, dễ chịu', 'robusta, arabica', 12, 3, 2, 2, '2019-10-26 03:00:00', NULL),
(42, 'Cà phê xay Vinacafé Black mạnh mẽ và nồng nhiệt', 83000, 'vinacoffee-black.jpg', 200, 'Cà phê xay vinacafé là dòng sản phẩm cà phê phin mang phong cách Việt Nam, mạnh mẽ và nồng nhiệt. Vinacafé tôn vinh hương vị tuyệt hảo đặc trưng của robusta Việt Nam. CÀ phê xay Vinacafé Black 100% từ hạt cà phê nguyên chất, không pha tạp chất phụ gia tạo màu, tạo hương , đảm bảo sức khỏe người tiếu dùng', 'đắng, đen, đậm đà, chất của người Việt Nam', '100% robusta', 12, 3, 2, 3, '2019-10-26 03:00:00', NULL),
(43, 'Cà Phê Sữa Hoà Tan Café deVIN 3in1 (Túi 30 gói x 17g)', 69000, 'deVIN3in1510g.jpg', 510, 'Cà phê hòa tan deVIN 3 trong 1 đậm đà hài hòa được pha trộn theo tỷ lệ thích hợp cùng sữa và bột kem, đáp ứng sở thích thưởng thức của đại đa số người tiêu dùng\r\nSản phẩm mang tới một trải nghiệm đặc biệt cho những tín đồ cà phê tại Việt Nam, bên cạnh vị thơm ngon khó cưỡng, điều khác biệt so với cà phê hòa tan thông thường đó là bạn sẽ thấy được lớp cà phê rang xay nhuyễn mỏng mịn còn đọng lại dưới đáy ly cà phê, là minh chứng cho cà phê rang xay nghiền nhuyễn.\r\nSản phẩm được làm từ 100% hạt cà phê Việt Nam chất lượng cao\r\n', 'Thơm, ngon, mùi vị dễ chịu', 'Đường, caramel, bột kem, cà phê hoà tan (12%), sữa bột, muối, hương cà phê', 12, 4, 1, 3, '2019-10-26 03:00:00', NULL),
(44, 'Cà Phê Đường Kiêng Café deVIN 3in1 (Hộp 10 gói x 17g)', 35000, 'deVIN3in1duongkieng.jpg', 170, 'cà Phê Đường Kiêng Café deVIN 3in1 chắt lọc từ những hạt cà phê ngon nhất cùng bí quyết và công nghệ sản xuất hiện đại, Cà phê hương chồn 3.	Cà Phê Đường Kiêng Café deVIN 3in1 mang đến cho người dùng những trải nghiệm đầy tinh tế. Bên cạnh đó, với chiết xuất từ những hạt cà phê nguyên chất, sản phẩm đem đến những ly cà phê sánh quyện, đậm đặc cùng hương chồn thơm nồng nàn quyến rũ, giúp bạn luôn tỉnh táo để hoàn thành tốt mọi công việc.\r\nHãy khởi đầu ngày mới thật hăng hái cùng 3.	Cà Phê Đường Kiêng Café deVIN 3in1  với hương thơm cà phê ngất ngây, kết hợp vị hài hòa của cà phê và bột kem thơm mát, mang lại cho bạn sự tươi tỉnh, lạc quan để làm tốt những những công việc quan trọng và tận hưởng cuộc sống nhiều hơn.\r\n', 'Thơm, ngon, mùi vị nhẹ, ít đường', ': Đường kiêng, Acesulfame K, caramel, Isomalt, bột kem, cà phê hoà tan (12%), sữa bột, muối, hương cà phê', 12, 4, 1, 1, '2019-10-26 03:00:00', NULL),
(45, 'Cà Phê Đen Hoà Tan Espresso Café deVIN 2in1 (Hộp 10 gói x 16g)', 29000, 'deVIN2in1160g.jpg', 160, 'Cà Phê Đen Hoà Tan Espresso Café deVIN 2in1  , cà phê có nước pha màu nâu cánh gián đậm, mùi thơm nhẹ, vị đắng hơi gắt đặc trưng của giống cà phê Robusta.\r\nĐược chế biến từ những hạt cà phê Culi Robusta ngon nhất của vùng đất Thánh địa cà phê Buôn Ma Thuột cùng những công nghệ hiện đại nhất thế giới và bí quyết riêng không thể sao chép, Sáng tạo số 1 mang hương thơm dịu lưu luyến, vị đậm đà mân mê vị giác chuyên dành cho những người sành cà phê và các quán cà phê muốn sở hữu gu cà phê riêng biệt.\r\nĐặc biệt, phối trộn các loại theo công thức và hướng dẫn đặc biệt từ các chuyên gia cà phê deVin còn có thể giúp tạo ra gu uống cà phê riêng, mang hương vị đặc trưng chỉ có ở quán bạn.\r\n', 'Chất đen, gu mạnh ', 'Đường, maltodextrin, cà phê hoà tan (25%), muối, hương cà phê.', 12, 4, 1, 1, '2019-10-26 03:00:00', NULL),
(46, 'Cà Phê Đen Hoà Tan Espresso Café deVIN 2in1 (Túi 30 gói x 16g)', 89000, 'deVIN2in1.jpg', 480, 'Cà Phê Đen Hoà Tan Espresso Café deVIN 2in1  , cà phê có nước pha màu nâu cánh gián đậm, mùi thơm nhẹ, vị đắng hơi gắt đặc trưng của giống cà phê Robusta.\r\nĐược chế biến từ những hạt cà phê Culi Robusta ngon nhất của vùng đất Thánh địa cà phê Buôn Ma Thuột cùng những công nghệ hiện đại nhất thế giới và bí quyết riêng không thể sao chép, Sáng tạo số 1 mang hương thơm dịu lưu luyến, vị đậm đà mân mê vị giác chuyên dành cho những người sành cà phê và các quán cà phê muốn sở hữu gu cà phê riêng biệt.\r\nĐặc biệt, phối trộn các loại theo công thức và hướng dẫn đặc biệt từ các chuyên gia cà phê deVin còn có thể giúp tạo ra gu uống cà phê riêng, mang hương vị đặc trưng chỉ có ở quán bạn.\r\n', 'Chất đen, gu mạnh.', 'Đường, maltodextrin, cà phê hoà tan (25%), muối, hương cà phê.', 12, 4, 1, 3, '2019-10-28 03:00:00', NULL),
(47, 'Cà phê deVIN Cappuccino 160g', 59000, 'deVINcappu.jpg', 160, 'Cà phê deVIN Cappuccino160g, dành cho những người thích hương vị cà phê đậm đà, mạnh như cà phê rang xay.\r\nĐược sản xuất trên công nghệ hàng đầu, chiết xuất từ những phần tinh túy nhất có trong từng hạt cà phê và bí quyết không thể sao chép, deVIN cho ra đời sản phẩm cà phê Cappuccino dành cho những người thích hương vị cà phê đậm đà, sữa tươi béo, ngọt, dịu dàng.\r\n', 'chất cà phê sữa, hòa quyện vị sữa tươi cappuccino.', 'Đường, maltodextrin, cà phê hoà tan (25%), muối, hương cà phê, sữa tươi.', 12, 4, 1, 1, '2019-10-28 03:00:00', NULL),
(48, 'Cà phê deVIN vị Hạnh Nhân 160g', 59000, 'deVINhanhnhan.jpg', 160, 'Cà phê deVIN Cappuccino160g, dành cho những người thích hương vị cà phê đậm đà, mạnh như cà phê rang xay cùng với hương vị hạnh nhân tốt cho sức khỏe.\r\nĐược sản xuất trên công nghệ hàng đầu, chiết xuất từ những phần tinh túy nhất có trong từng hạt cà phê và bí quyết không thể sao chép, deVIN cho ra đời sản phẩm cà phê vị hạnh nhân dành cho những người thích hương vị cà phê đậm đà, sữa tươi béo, mùi vị hạnh nhân đặc trưng.\r\n', 'chất cà phê , hòa quyện vị hạnh nhân đặc trưng.', 'Đường, maltodextrin, cà phê hoà tan (25%), muối, hương cà phê, hạnh nhân(5%)', 12, 4, 1, 1, '2019-10-28 03:00:00', NULL),
(49, 'CÀ PHÊ SỮA VINACAFE  (26GÓI X 20GR/GÓI)', 49000, 'vinacoffee-sua.jpg', 520, 'Hương vị nguyên bản vượt trội thời gian\r\nHơn 45 năm tại Vinacafe, chúng tôi cam kết không làm mất đi sự nguyên bản trong từng hương từng vị cà phê bằng bí quyết công thức hạt 100%, cho mỗi tách cà phê hoà tan 3 in 1 Vinacafe Gold Original là sự thưởng thức cà phê ngon đúng nghĩa.\r\n', 'Cà phê chất mạnh, tinh hoa hương vị từ 100% cà phê việt.', 'Đường, cafe hòa tan, bột kem thực vật, chất ổn định, chất nhũ hóa, chất chống đông vón, muối.', 24, 3, 1, 3, '2019-10-28 03:00:00', NULL),
(50, 'Vinacafe Chất - Sài Gòn Cà Phê Sữa Đá (Hộp 10 Gói x 29G)', 38000, 'vinacoffee-chat.jpg', 290, 'Vinacafe Chất - Sài Gòn Cà Phê Sữa Đá được chắt lọc từ những hạt cà phê nguyên chất mang đến hương vị đậm đà trong từng tách cà phê. Hương thơm ngây ngất, cho bạn sự tỉnh táo và tràn đầy năng lượng để bắt đầu ngày mới.\r\nVinacafe Chất - Sài Gòn Cà Phê Sữa Đá là sản phẩm cà phê sữa đến từ thương hiệu Vinacafe.\r\nVinacafe Chất - Sài Gòn Cà Phê Sữa Đá được làm từ cà phê nguyên chất nên mang đến chất lượng tuyệt hảo. Sản phẩm vẫn giữ được vị đắng đặc trưng và hương thơm đậm đà. Vinacafe Chất sẽ trở thành nguồn năng lượng, mang đến sự tỉnh táo cho bạn sẵn sàng giải quyết mọi công việc.\r\n', 'Vinacafe Chất Sài Gòn Cà PhêSữa Đá được chắt lọc từ những hạtcà phê nguyên chất mang đến hương vị đậm đà trong từng táchcà phê.', 'Đường, Bột kem pha cà phê, Melnodextrin, Hương cafe tự nhiên.', 24, 3, 1, 1, '2019-10-28 03:00:00', NULL),
(51, 'Hộp 48 viên nén Vinacafe De Nam', 480000, 'vinacoffee-viennen.jpg', 326, ': Viên Nén Cà Phê Vinacafe de Nam là sản phẩm tương thích với máy pha cà phê Vinacafe – Café de Nam 100% nguyên chất (Cà phê Arabica và Robusta).\r\nSản phẩm là những hạt cafe được tuyển chọn từ những hạt cà phê chính mọng kỹ từ các quốc gia nổi tiếng về cafe trên thế giới\r\nCafe viên nén được pha chế bởi máy pha cafe sẽ tạo ra một ly cafe với lớp kem dầy, thơm, béo và đầy hấp dẫn.\r\nMỗi gói gồm 48 viên nén.\r\n', '1 viên – Sành Điệu(gu nhẹ) , 2 viên – Bản Lĩnh (gu đậm).', 'Robusta và Arabica', 12, 3, 2, 1, '2019-10-28 03:00:00', NULL),
(52, 'Gói 24 viên nén Vinacafe De Nam', 239000, 'vinacoffee-viennen24.jpg', 168, 'Viên Nén Cà Phê Vinacafe de Nam là sản phẩm tương thích với máy pha cà phê Vinacafe – Café de Nam 100% nguyên chất (Cà phê Arabica và Robusta).\r\nSản phẩm là những hạt cafe được tuyển chọn từ những hạt cà phê chính mọng kỹ từ các quốc gia nổi tiếng về cafe trên thế giới\r\nCafe viên nén được pha chế bởi máy pha cafe sẽ tạo ra một ly cafe với lớp kem dầy, thơm, béo và đầy hấp dẫn.\r\nMỗi gói gồm 24 viên nén.\r\n', '1 viên – Sành Điệu(gu nhẹ) , 2 viên – Bản Lĩnh (gu đậm).', 'Robusta và Arabica', 12, 3, 2, 3, '2019-10-28 03:00:00', NULL),
(53, 'Cà phê sữa hòa tan NesCafé 3 trong 1 sánh đậm 240g (24g x 10 gói)', 35000, 'nes3in1sanhdam.jpg', 240, 'Cà phê sữa hòa tan NesCafe 3 trong 1 sánh đậm hộp 10 gói mang trong mình vị đắng quen thuộc và hương thơm tự nhiên vốn có, sản phẩm được tạo ra để mang đến niềm đam mê về một loại cà phê nguyên chất và thuần khiết.\r\nBạn sẽ cảm nhận được cái hương vị đậm đà rất Việt Nam, cái vị đắng dịu nhẹ ban đầu và \"dư âm\" của hậu vị cà phê đọng lại nơi cổ họng không thể nào quên.\r\n', 'Đặc trưng với vị béo ngọt của sữa, hoà quyện với hương vị cà phê đậm đà.', 'Đường, bột kem cà phê, hỗn hợp cà phê hòa tan, maltodextrin, chất làm đày 1440, muối, màu tổng hợp.', 24, 2, 1, 1, '2019-10-28 03:00:00', NULL),
(54, 'NESCAFÉ 3in1 Cà phê Sôcôla 200g (10 gói x 20g)', 35000, 'nes3in1socola.jpg', 200, 'NESCAFÉ 3in1 Cà phê Sôcôla được điều chế từ những hạt cà phê nguyên chất, được tuyển chọn kỹ lưỡng, mang đến cho bạn những ly cà phê ngon tuyệt, đậm vị socola.\r\nLoại cà phê này được rang theo kỹ thuật hiện đại ở nhiệt độ lý tưởng, đảm bảo dậy mùi và có hương vị thơm ngon hảo hạng.\r\nSự kết hợp hoàn hảo từ 100% cà phê thuần tuý Việt Nam hoà quyện với sữa thật và bột ca cao chất lượng, NESCAFÉ 3IN1 Cà Phê Sôcôla mang đến trải nghiệm cà phê mới thơm ngon khó cưỡng.\r\nNESCAFÉ 3IN1 Cà Phê Sôcôla, phiên bản mash-up giữa cà phê với ca cao cho vị đắng hòa quyện với vị ngọt của sữa thật, đem lại ly cà phê thơm ngon như pha chế tại coffee shop ngay tại nhà.\r\n', 'Sự kết hợp hoàn hảo từ 100% cà phê thuần tuý Việt Nam hoà quyện với sữa thật và bột ca cao chất lượng.', '100% cà phê việt', 24, 2, 1, 1, '2019-10-28 03:00:00', NULL),
(55, 'Nescafe Cappuccino Vị Caramel (hộp 10 gói x 20 g)', 60000, 'nes3in1caramel.png', 200, 'NESCAFÉ Cappuccino vị Caramel - thơm ngon \"chuẩn gu\" như ly Cappuccino được pha tại quán cà phê.Mô tả sản phẩm.\r\nNESCAFÉ Cappuccino vị Caramel được làm từ 100% hạt cà phê Robusta Việt Nam kết hợp cùng bột kem nhập khẩu từ Nhật Bản với công nghệ Booster độc quyền từ Nestlé mang đến cho bạn ly Cappuccino thơm ngon, vị sữa béo đặc trưng, cùng lớp bọt sữa sánh mịn \"chuẩn gu\" như được pha bằng máy tại quán cà phê bạn yêu thích. NESCAFÉ Cappuccino vị Caramel - Chuẩn gu cà phê quán.\r\n', 'Chất cà phê hòa lẫn vị kém sánh mịn.', 'Đường, bột kem, sữa bột tách kem, muối i-ốt, chất điều chỉnh độ chua 500.', 24, 2, 1, 1, '2019-10-28 03:00:00', NULL),
(56, 'Cà Phê Hòa Tan Latte Macchiato Nescafe Gold (20.5g x 10 Gói)', 73000, 'nesgoldlatte.jpg', 205, 'Cà Phê Hòa Tan Latte Macchiato Nescafe Gold đây là dòng sản phẩm cà phê hòa tan cao cấp được phát triển theo gu thưởng thức cà phê ngày càng đa dạng của người Việt.\r\nSản phẩm được tạo ra từ những hạt cà phê Arabica và Robusta chất lượng cao cùng với sữa chất lượng, xử lý bằng công nghệ tạo bọt độc quyền của NESCAFÉ để tạo nên lớp bọt sữa dày mịn màng, quyến rũ của ly cà phê Ý đúng điệu.\r\nVới cách đóng gói tiện lợi, việc pha chế dễ dàng, giờ đây, bạn có thể thưởng thức tách cà phê Ý yêu thích ở bất cứ nơi đâu, bất cứ lúc nào cùng NESCAFÉ GOLD như bữa sáng đầy cảm hứng, buổi trưa cần chút thư giãn, buổi chiều cần nạp chút sáng tạo...\r\n', 'cà phê chất, thoang thoảng vị sữa tươi.', 'Kết hợp từ cà phê Arabica và Robusta cùng với sữa được xử lý bằng công nghệ tạo bọt độc quyền của Nescafé.', 24, 2, 1, 1, '2019-10-28 03:00:00', NULL),
(57, 'Cà Phê Hòa Tan Cappuccino Nescafe Gold (20.5g x 10 Gói)', 79300, 'nesgoldcappu.jpg', 205, 'Cà Phê Hòa Tan Cappuccino Nescafe Gold đây là loại sản phẩm cà phê hòa tan cao cấp được phát triển theo gu thưởng thức cà phê ngày càng nhiều của người Việt.\r\nSản phẩm được tạo ra từ các hạt cà phê Arabica và Robusta chất lượng cao cùng với sữa chất lượng, xử lý bằng khoa học tạo bọt độc quyền của NESCAFÉ để tạo nên lớp bọt sữa dày dặn, mịn màng, quyến rũ của ly cà phê Ý sành điệu.\r\nSản phẩm được đóng gói tiện lợi, việc pha chế tiện dụng, giờ đây, bạn mang thể thưởng thức tách cà phê Ý đúng điệu ở bất cứ nơi đâu, bất cứ khi nào khi có NESCAFÉ GOLD như bữa sáng đầy cảm hứng, buổi trưa cần chút thư giãn, buổi chiều cần nạp chút thông minh…Nescafe tự hào ra mắt cái sản phẩm mới Nescafe Gold sở hữu mong muốn đáp ứng gu thưởng thức cà phê ngày càng tinh tế của người Việt và đem đến cho bạn 1 tuyển lựa mới: 1 hương vị cà phê Ý đúng chuẩn sở hữu lớp bọt sữa mịn màng cùng hương cà phê tinh tế chỉ với hai phút chuẩn bị.Sản phẩm được áp dụng các khoa học tiên tiến nhất để bổ sung thêm cà phê được rang và  xay nhuyễn giúp mang đến vị cà phê tinh tế của tinh hoa cà phê Arabica và Robusta theo tỷ lệ phối trộn xuất sắc, đúng phong cách Âu Châu.  Nguồn sữa chất lượng cao từ Úc và New Zealand kết hợp cùng công nghệ tạo bọt độc quyền của Nestle tạo nên lớp bọt sữa mịn màng, sánh quyện, vị béo ngọt ngào.ngoài ra với bao so bì đẳng cấp, chất lượng cao cấp thì đây cũng sở hữu thể là món quà đầy ý nghĩa để bạn gửi trao thành ý tới các người quan yếu trong cuộc sống của mình đó.\r\n', 'Cà phê thơm, lớp bọt sữa mịn màng, sánh quyện, vị béo ngọt ngào.', 'Kết hợp từ cà phê Arabica và Robusta cùng với sữa được xử lý bằng công nghệ tạo bọt độc quyền của Nescafé.', 24, 2, 1, 1, '2019-10-28 03:00:00', NULL);
INSERT INTO `coffees` (`id`, `name`, `price`, `image`, `capacity`, `info`, `specific`, `ingredient`, `expired`, `id_brand`, `id_type`, `id_unit`, `created_at`, `updated_at`) VALUES
(58, 'Hộp cà phê hòa tan Nescafe Gold White Espresso (10 gói x 25g)', 87000, 'nesgoldwhite.jpg', 205, 'Nescafe Gold cà phê phong cách châu Âu với tỉ lệ phối trộn Arabica cao hơn Robusta đem đến 1 ly cafe chuẩn Ý cao cấp.\r\n\r\nHương vị tinh tế, hương thơm khó cưỡng cùng với một chút cafe rang xay nguyễn tạo nên sự độc đáo cho Nescafé Gold White Espresso.\r\n\r\nLớp bột nhẹ nhàng, mịn màng tạo cảm giác thoải mái và thích thú khi thưởng thức sản phẩm.\r\n\r\nBạn có thể sử dụng Nescafé gold White Espresso vào những lúc thư giãn, nghỉ ngơi để tận hưởng cuộc sống.', 'Thơm, ngon, tinh tế với rang xay nhuyễn.', ' Đường, bột kem (dầu thực vật hydro hóa, sữa bột tách kem, xirô glucose, chất điều chỉnh độ chua (340(ii), 452(i), 331(iii)), natri caseinat, chất chống đông vón 551.', 24, 2, 1, 1, '2019-10-28 03:00:00', NULL),
(59, 'Nescafé Việt - Cà Phê Đen Đá(15 gói x 16gr)', 49500, 'nesdenda.jpg', 240, 'Cà Phê NESCAFE Việt Cà phê Đen Đá đem đến cho người thưởng thức một hương vị thuần túy Việt Nam với vị cà phê mạnh mẽ, nguyên chất. Hương vị cà phê đen đậm đà và mạnh mẽ với hương thơm lôi cuốn, cho bạn thỏa mãn niềm đam mê thưởng thức cà phê đen đá thuần túy Việt Nam với một phong cách hiện đại. Sản phẩm chất lượng cao và hợp vệ sinh, được sản xuất theo công nghệ hiện đại, không sử dụng các chất hóa học, chất gây hại cho sức khỏe, nên bạn hoàn toàn yên tâm sử dụng. Sản phẩm được đóng gói nhỏ trong hộp tiện dụng, giúp bạn không mất quá nhiều thời gian pha chế, tiện cho bạn khi thưởng thức cà phê ở văn phòng hoặc khi đi chơi xa, du lịch.', 'Chất đen cà phê, gu mạnh, sản khoái một ngày dài.', 'Đường, hỗn hợp cà phê hòa tan, muối, chất điều chỉnh độ chua, hương tổng hợp', 24, 2, 1, 1, '2019-10-27 20:00:00', NULL),
(60, 'Nescafé Café Việt Cà Phê Đen Đá Ít Đường (12 gói x 7 gr)', 30000, 'nesdendaitduong.jpg', 84, 'Cà phê đen đá NesCafe Việt mang trong mình vị đắng quen thuộc và hương thơm tự nhiên vốn có, nay được bổ sung thêm hàm lượng cà phê nguyên chất, cho hương vị cực mạnh, thơm ngon gấp bội.\r\n\r\nSản phẩm được tạo ra để mang đến niềm đam mê về một loại cà phê nguyên chất và thuần khiết. Bạn sẽ cảm nhận được hương vị đậm đà rất Việt Nam, vị đắng dịu nhẹ ban đầu và \"dư âm\" của vị cà phê đọng lại nơi cổ họng không thể nào quên.\r\n\r\nCòn gì tuyệt vời hơn khi khởi đầu ngày mới với một ly NesCafe Việt cà phê đen đá, hớp một ngụm đặc trưng vị cà phê mạnh mẽ, bạn như được khơi dậy nguồn hứng khởi cho thể chất và tinh thần. Cũng nhờ đó, mọi câu chuyện, mọi kết nối được chia sẻ dễ dàng hơn.', 'Vị cà phê chuẩn đen, dành cho gu cà phê mạnh nhưng vẫn ít đường.', 'Đường(10%), hỗn hợp cà phê hòa tan, muối, chất điều chỉnh độ chua, hương tổng hợp', 24, 2, 1, 1, '2019-10-27 20:00:00', NULL),
(61, 'Cà phê đen hòa tan NesCafé Red Cup 30g (2g x 15 gói)', 21000, 'nesredcup.jpg', 30, 'Cà phê hòa tan nguyên chất Red Cup của thương hiệu Nescafe được làm từ cà phê nguyên chất nên mang đến chất lượng tuyệt hảo. Sản phẩm vẫn giữ được vị đắng đặc trưng và hương thơm đậm đà của cà phê, thích hợp dành cho những giờ thưởng thức cà phê bên bạn bè hay đồng nghiệp.\r\n\r\nCà phê hòa tan nguyên chất Nescafe Red Cup sẽ trở thành nguồn năng lượng, mang đến sự tỉnh táo và tăng cảm hứng sáng tạo cho một ngày làm việc hiệu quả. Cà phê luôn là thức uống hàng đầu dành cho những người hay giải quyết nhiều công việc.', 'Hương vị tuyệt hảo, không đường thích hợp sử dụng trong khi làm việc.', '100% bột cà phê hòa tan', 24, 2, 1, 1, '2019-10-27 20:00:00', NULL),
(62, 'Cà phê đen hòa tan NesCafé Café Việt vị mạnh hộp (3g x 12 gói)', 24500, 'nesvimanh.jpg', 36, 'Được làm từ 100% hạt cà phê chất lượng cùng chương trình Nescafe Plan. Nescafe tự hào mang đến cho bạn một ly cà phê đậm đà, vị mạnh đúng chuẩn thuần túy Việt Nam, làm cho người thưởng thức bừng tỉnh tức thì, tăng sự tập trung, sảng khoái tinh thần.\r\nThành phần từ cà phê nguyên chất, tuyển chọn kỹ lưỡng từ những hạt cà phê chất lượng cao, được rang theo kỹ thuật hiện đại ở nhiệt độ lý tưởng, đảm bảo dậy mùi và có hương vị thơm ngon hảo hạng, phù hợp với khẩu vị thưởng thức của người Việt.\r\nHương vị cà phê đen đậm đà hòa với hương thơm lôi cuốn sẽ cho bạn cảm giác thỏa mãn, hài lòng nhất dù uống nóng hay uống đá.\r\n', 'Vị đậm mạnh, không đường, cho bạn tuỳ ý đong đếm khẩu vị phù hợp.', '100% từ hạt cà phê Việt Nam', 24, 2, 1, 1, '2019-10-27 20:00:00', NULL),
(63, 'Gói cà phê đen hòa tan NesCafé Café Việt (16g x 35 gói)', 97000, 'nesdenda560g.jpg', 560, 'Cà phê đen đá NesCafe Việt mang trong mình vị đắng quen thuộc và hương thơm tự nhiên vốn có, nay được bổ sung thêm hàm lượng cà phê nguyên chất, cho hương vị cực mạnh, thơm ngon gấp bội.\r\nSản phẩm được tạo ra để mang đến niềm đam mê về một loại cà phê nguyên chất và thuần khiết. Bạn sẽ cảm nhận được hương vị đậm đà rất Việt Nam, vị đắng dịu nhẹ ban đầu và \"dư âm\" của vị cà phê đọng lại nơi cổ họng không thể nào quên.\r\nCòn gì tuyệt vời hơn khi khởi đầu ngày mới với một ly NesCafe Việt cà phê đen đá, hớp một ngụm đặc trưng vị cà phê mạnh mẽ, bạn như được khơi dậy nguồn hứng khởi cho thể chất và tinh thần. Cũng nhờ đó, mọi câu chuyện, mọi kết nối được chia sẻ dễ dàng hơn.\r\n', 'Vị đậm mạnh, có đường', 'Đường, hỗn hợp cà phê hòa tan, muối, chất điều chỉnh độ chua, hương tổng hợp', 24, 2, 1, 3, '2019-10-27 20:00:00', NULL),
(64, 'Hộp cà Phê Nescafé Red Cup (200g)', 115000, 'nesredcuphop.jpg', 200, 'Cà Phê Nescafé Red Cup được điều chế từ những cà phê nguyên chất, tuyển chọn kỹ lưỡng từ những hạt cà phê Việt Nam chất lượng cao. Sau khi chọn lọc, cà phê sẽ được xử lý bằng công nghệ ERA hiện đại. giúp giữ lại trọn vẹn hương vị nguyên thủy của từng hạt cà phê, cho bạn thưởng thức mùi thơm và vị tinh tế nhất.\r\nCà Phê Nescafé Red Cup là lựa chọn hoàn hảo dành cho người muốn được thưởng thức cà phê cổ điển, thuần khiết và không pha trộn. Trong sản phẩm tuyệt đối không chứa đường, sữa hay bất kỳ nguyên liệu nào, giữ lại vị đắng và thơm lừng chính thống của cà phê nguyên chất.\r\nCà Phê Nescafé Red Cup (200g) là lựa chọn lý tưởng dành cho người muốn được thưởng thức cà phê cổ điển, thuần khiết và không pha trộn. Sản phẩm được đóng hũ sang trọng, nhỏ gọn, có thể mang theo để sử dụng ở văn phòng hoặc đi chơi xa, du lịch.\r\n', '100% nguyên chất không pha sẵn (không đường, không sữa) dành cho người muốn được thưởng thức cà phê cổ điển, thuần khiết và không pha trộn.', '100% bột cà phê hòa tan', 24, 2, 1, 1, '2019-10-27 20:00:00', NULL),
(65, 'Thức Uống Sữa Cà Phê Nescafé Smoovlatte (225ml)', 16000, 'nessmoovlatte.jpg', 225, 'Thức Uống Sữa Cà Phê Nescafé Smoovlatte khơi nguồn hứng khởi cho ngày mới năng động, vị cà phê sữa thơm ngon, ẩn hiện tinh tế, hòa tan trong vị béo ngọt ngào.\r\nĐược điều chế từ những cà phê nguyên chất, tuyển chọn kỹ lưỡng từ những hạt cà phê Việt Nam chất lượng cao.\r\nThiết kế dạng chai tiện lợi, dễ dàng mang đi xa, mang lại cho bạn một tinh thần sảng khoái để khởi đầu cho ngày mới năng động, đồng thời giúp bạn làm việc hiệu quả với một tinh thần tập trung cao.\r\n', 'Thức uống sữa cà phê dạng chai tiện lợi được làm từ sữa thật hòa quyện cùng cà phê ', 'Nước, nước cốt hỗn hợp cà phê rang xay, đường, bột kem pha cà phê,...', 12, 2, 3, 2, '2019-10-29 20:00:00', NULL),
(66, 'Thức Uống Sữa Cà Phê Nescafé Smoovlatte Mocha (225ml)', 14000, 'nessmoovlattemocha.jpg', 225, 'Thức Uống Sữa Cà Phê Nescafé Smoovlatte Mocha thiết kế dạng chai tiện lợi, dễ dàng mang đi xa.\r\nSữa thật, cà phê thật, cho ngày mới tươi tỉnh.\r\nHương cà phê dịu nhẹ, vị dịu ngọt của sữa tươi và chocolate.\r\nKhơi nguồn hứng khởi cho ngày mới năng động.\r\n', 'thức uống sữa cà phê dạng chai tiện lợi được làm từ sữa thật, cà phê thật có thêm hượng vị socola.', 'Nước, nước cốt hỗn hợp cà phê rang xay, đường, bột kem pha cà phê,...', 12, 2, 3, 2, '2019-10-29 20:00:00', NULL),
(67, 'Bột kem pha cà phê Coffee Mate French Vanilla (425g)', 150000, 'nesbotkemvanilla.jpg', 425, 'Hàng quán cafe luôn kích thích bạn ghé thăm không chỉ thu hút bạn bởi không gian thơ mộng, độc lạ hay để bạn thoả sức “sống ảo”. Mỗi ly cà phe còn mang đến cho bạn sự hấp dẫn riêng của nó bởi hương vị đặc trưng của từng quán.\r\nNhăm nhi 1 tách cafe cùng người thân, bạn bè, cảm giác tự tay pha thật tuyệt vời và ý nghĩa biết bao. Bí quyết để có ly cafe vừa chất lượng, vừa thơm béo là bột kem pha cafe Pháp Nestle.', 'Thơm, béo, thoang thoảng vị vanilla,thích hợp dùng chung với cà phê.', 'Bột kem, sữa, muối, chất điều chỉnh…', 24, 2, 4, 1, '2019-10-29 20:00:00', NULL);

